$(document).ready(function () {
    $(".codemirror-textarea").val($.trim($(".codemirror-textarea").val()));
    let code = $(".codemirror-textarea")[0];
    let editor = CodeMirror.fromTextArea(code, {
        lineNumbers: true,
        lineWrapping: false,
        styleActiveLine: true,
        styleActiveSelected: true,
        mode: 'application/json',
        gutters: ["CodeMirror-lint-markers"],
        lint: true,
        scrollbarStyle: 'native'
    });
    let $json = "";
    $('#validate').click(function () {
        try {
            $json = editor.getValue();
            if (IsURL($json.toString())) {
                $.getJSON($json.toString(), {}, function (data, status) {
                    if (status == "success") {
                        let $dat = JSON.stringify(data);
                        editor.setValue($dat);
                        $djson = JSON.parse($dat);
                        $('#error').show();
                        $('#error').removeClass("alert-danger");
                        $('#error').addClass("alert-success");
                        $("#error").empty().append("Valid Json");
                        saveJson($json);
                    }
                    else {
                        alert("Nothing Found on URL");
                    }
                });
            } else {
                $djson = $json.toString();
                $djson = jsonlint.parse($djson);
                $('#error').show();
                $('#error').removeClass("alert-danger");
                $('#error').addClass("alert-success");
                $("#error").empty().append("Valid Json");
                saveJson($json);
            }
        }
        catch (err) {
            let lineNumber = err;
            let regex = /[+-]?\d+(?:\.\d+)?/g;
            let str = lineNumber.toString();
            let lin;
            let match;
            while (match = regex.exec(str)) {
                lin = match[0];
                console.log(match[0]);
                break;
            }
            $(".CodeMirror-line:eq(" + (lin - 1) + ")").css("background-color", "#f8d7da");
            $('#error').show();
            $('#error').removeClass("alert-success");
            $('#error').addClass("alert-danger");
            $("#error").empty().append(err);
        }
    })

    $('#reset').click(function () {
        editor.setValue('');
        $('#error').hide();
    })

    function IsURL(str) {
        let regex = /^(http:\/\/www\.|https:\/\/www\.|http:\/\/|https:\/\/)?[a-z0-9]+([\-\.]{1}[a-z0-9]+)*\.[a-z]{2,5}(:[0-9]{1,5})?(\/.*)?$/;
        return !!(regex.test(str));
    }

    function saveJson($json) {
        let input = {
            'key': $("#key").val(),
            'value': $json,
            'path': $("#path").val()
        };
        $.ajax({
            type: "POST",
            url: 'api/configapi/savejson',
            dataType: 'json',
            contentType: 'application/json',
            data: JSON.stringify(input),
            success: function (response) {
                console.log('success', response);
                window.location.href = 'home/index';
            },
            error: function (error) {
                console.log('error', error);
            }
        })
    }
})